import React, { Component } from "react";
import items from "./data";

const RoomContext = React.createContext();

class RoomProvider extends Component {
  state = {
    rooms: [],
    sortedRooms: [],
    featuredRooms: [],
    bookedRooms:[],
    loading: true,
    type:'all',
    slug,
    capacity:1,
    price:0,
    minPrice:0,
    maxPrice:0,
    place:"Shimla",
    // breakfast:false,
    // pets:false
  };

  //getData

  componentDidMount() {
    let rooms = this.formatData(items);
    // console.log(rooms);
    let featuredRooms = rooms.filter(room => room.featured === true);

    //Calculating the Max Price from all the rooms
    let maxPrice = Math.max(...rooms.map(item =>item.price))

    //Booked-Rooms
    let bookedRooms = rooms.filter(room => room.slug === slug)

    

    
    this.setState({
      rooms,
      featuredRooms,
      sortedRooms: rooms,
      bookedRooms,
      loading: false,
      price:maxPrice,
      maxPrice,
    //   maxSize
    // place
    });
  }

  formatData(items) {
    let tempItems = items.map((item) => {
      let id = item.sys.id;
      let images = item.fields.images.map((image) => image.fields.file.url);
      let room = { ...item.fields, images, id };
      return room;
    });
    return tempItems;
  }

  getRoom = (slug) => {
      let tempRooms = [...this.state.rooms];
      const room = tempRooms.find(room => room.slug === slug)
      return room;
  }


  handleChange= event =>{
      const target = event.target
      const value  = event.type ==='checkbox'? target.checked : target.value
      const name=event.target.name
      this.setState({
          [name]:value
      },this.filterRooms)
     
    //   console.log(type,name,value);
  }

  filterRooms = () =>{
    //   console.log("Hello");
    let {rooms,type,capacity,price,place} = this.state;

    //All the rooms
    let tempRooms =[...rooms];

    //Transfor values as the number in capacity get changed to string
    capacity=parseInt(capacity)


    //Transfor values as the number in price get changed to string
    price=parseInt(price)


    //filter by Type
    if(type !== 'all'){
        tempRooms = tempRooms.filter(room => room.type === type)
    }

    //filter by Capacity
    if(capacity !== 1){
        tempRooms = tempRooms.filter(room =>room.capacity >=capacity)
    }

    //filter by Place
    if(place !== 'None'){
        tempRooms = tempRooms.filter(room => room.place === place)
    }

    //filter by Price
    
        tempRooms = tempRooms.filter(room =>room.price <=price)
    

    this.setState({
        sortedRooms:tempRooms
    })

  }


  //Booked-Rooms


  filterRooms = () =>{
    //   console.log("Hello");
    let {rooms,type,slug,capacity,price,place} = this.state;

    //All the rooms
    let tempRooms =[...rooms];

    //filter by Type
    if(type !== 'all'){
      tempRooms = tempRooms.filter(room => room.slug === slug)
  }
  
    

    this.setState({
        bookedRooms:tempRooms
    })

  }




  render() {
    return (
      <RoomContext.Provider value={{ ...this.state,getRoom:this.getRoom,handleChange:this.handleChange }}>
        {this.props.children}
      </RoomContext.Provider>
    );
  }
}

const RoomConsumer = RoomContext.Consumer;

//Higher Order Component
export function withRoomConsumer(Component){
    return function ConsumerWrapper(props){
        return <RoomConsumer>
            {value => <Component {...props} context={value}/>}
        </RoomConsumer>
    }
}




export { RoomProvider, RoomConsumer, RoomContext };
